import { useEffect, useState } from 'react';
import './App.css';

function App() {
  const [data, setData] = useState([])
  const [value, setValue] = useState("")

  useEffect(() => {
    fetchData()
  }, [])

  const fetchData = async () => {
    const res = await fetch("http://localhost:8000/todos/")
      .then(response => response && response.json())
      .then(res => res && setData(res))
  }

  const sendDataToBE = async () => {
    if (value.trim().length) {
      const text = value.trim()
      text && await fetch("http://localhost:8000/todos/", {
        method: "POST",
        body: JSON.stringify({ text }),
        headers: {
          "Content-type": "application/json"
        }
      })
      fetchData()
      setValue("")
    }
  }

  const delteAnItem = async (id) => {
    console.log('the id ->', id)
    await fetch(`http://localhost:8000/todos/${id}`, { method: "DELETE" })
    fetchData()
  }

  const updateText = async (id) => {
    console.log("to update id ==>", id)
    const currentItem = data.find(item => item.id === id)
    setValue(currentItem.text)
  }

  return (
    <div className="App">
      <input type="text" placeholder='send some text' value={value} onChange={(e) => { setValue(e.target.value) }} />
      <button onClick={sendDataToBE}>send me to BE</button>
      <ul>
        {data ? data?.map(text =>
          <div>
            <li>{text.id} {text.text}
              <button onClick={(id) => { delteAnItem(text.id) }}>delete</button>
              <button onClick={(id) => { updateText(text.id) }}>update</button></li>
          </div>
        ) : <div>no data </div>
        }
      </ul>
    </div>
  );
}

export default App;
