import { useEffect, useState } from 'react';
import './App.css';

function App() {
  const [data, setData] = useState([])
  const [value, setValue] = useState("")

  useEffect(() => {
    fetchData()
  }, [])

  const fetchData = async () => {
    const res = await fetch("http://localhost:8000/todos/")
      .then(response => response && response.json())
      .then(res => res && setData(res))
  }

  const sendDataToBE = async () => {
    const text = value.trim()
    text && await fetch("http://localhost:8000/todos/", {
      method: "POST",
      body: JSON.stringify({ text }),
      headers: {
        "Content-type": "application/json"
      }
    })
    fetchData()
  }

  return (
    <div className="App">
      <input type="text" placeholder='send some text' value={value} onChange={(e) => { setValue(e.target.value) }} />
      <button onClick={sendDataToBE}>send me to BE</button>
      {data ? data?.map(text =>
        <div>
          <li> {text.text}</li>
        </div>
      ) : <div>no data </div>
      }
    </div>
  );
}

export default App;
